return items.map(item => {
  const { noticia_id, titulo, legenda, fonte, imagem, arquivo_base64, imagem_url, imagem_url2, status, nota } = item.json;
  const id = item.json.id;

  const data = new Date();
  const data_processamento = data.toLocaleDateString('pt-BR'); // formato: dd/mm/aaaa

  return {
    json: {
      noticia_id,
      data_processamento,
      titulo,
      legenda: legenda.replace(/\\n/g, '\n\n'), // <- agora gera 1 linha em branco entre blocos
      fonte,
      imagem,
      arquivo_base64,
      imagem_url,
      imagem_url2,     // <-- agora vai junto
      status,
      nota
    }
  };
});

const dataAgora = new Date();
const yyyy = dataAgora.getFullYear();
const mm = String(dataAgora.getMonth() + 1).padStart(2, '0');
const dd = String(dataAgora.getDate()).padStart(2, '0');
const hh = String(dataAgora.getHours()).padStart(2, '0');
const min = String(dataAgora.getMinutes()).padStart(2, '0');
const ss = String(dataAgora.getSeconds()).padStart(2, '0');

const base = `noticia_${yyyy}${mm}${dd}_${hh}${min}${ss}`;

// coleta TODAS as download_url (inclui download_url2) de TODOS os itens
function collectDownloadUrls(obj, acc = []) {
  if (!obj || typeof obj !== 'object') return acc;
  for (const [k, v] of Object.entries(obj)) {
    if ((k === 'download_url' || k === 'download_url2') && typeof v === 'string') acc.push(v);
    else if (v && typeof v === 'object') collectDownloadUrls(v, acc);
  }
  return acc;
}

// URLs únicas vindas de todos os itens (ramo img1 + ramo img2)
const urlsAll = Array.from(new Set(items.flatMap(i => collectDownloadUrls(i.json))));

// primeira e segunda URL do lote
const url1 = urlsAll[0] ?? null;
const url2 = urlsAll.find(u => u !== url1) ?? null;

return items.map((item, index) => {
  const sequencial = String(index + 1).padStart(3, '0');
  const noticia_id = `${base}_${sequencial}`;

  // se já vier setado no item, respeita; senão usa as do lote
  const imagem_url  = item.json.imagem_url  ?? item.json.download_url  ?? url1;
  const imagem_url2 = item.json.imagem_url2 ?? item.json.download_url2 ?? url2;

  return {
    json: {
      ...item.json,
      noticia_id,
      status: item.json.status ?? 'aguardando',
      arquivo_base64: `${noticia_id}.txt`,
      imagem_url,
      imagem_url2,
    }
  };
});

