// === Parâmetros ===
const THRESHOLD = 80;           // mínimo para aprovar
const MAX_ITENS = 30;           // máximo a retornar
const DECAY_HALF_LIFE_H = 48;   // meia-vida da recência (horas)
const DEDUPE_SIMILARITY = 0.85; // similaridade p/ dedupe (0..1)
const KEEP_SCORE_IN_OUTPUT = false; // true só p/ debug

// === Dicionários de relevância (tema tributário) ===
const KW = {
  forte: [
    // Básicos
    'tributo','tributos','tributário','tributária','tributárias','tributários',
    'imposto','impostos','taxa','taxas','tarifa','tarifas',

    // Impostos específicos
    'icms','ipi','iss','irpf','irpj','csll','pis','cofins',
    'iof','iptu','itbi','itr','cide','inss','fgts','sat',

    // Reforma e mudanças
    'reforma tributária','sistema tributário','código tributário','ctn',

    // Fisco e fiscalização
    'fisco','arrecadação','fiscalização','autuação','multa','multas',
    'execução fiscal','notificação fiscal','cobrança tributária',

    // Planejamento e elisão
    'planejamento tributário','elisão fiscal','evasão fiscal','sonegação',

    // Incentivos e créditos
    'incentivo fiscal','incentivos fiscais','benefício fiscal','benefícios fiscais',
    'crédito tributário','compensação tributária','recuperação de crédito',

    // Órgãos e julgamentos
    'carf','stf','stj','receita federal','sefaz','pgfn',
    'procuradoria da fazenda','secretaria da fazenda',

    // Regimes e enquadramentos
    'simples nacional','lucro presumido','lucro real','lucro arbitrado','mei',

    // Obrigações acessórias
    'declaração de renda','sped','efd','ecf','dctf','dirf','perdcomp','nf-e','nota fiscal',
    'obrigações acessórias','bloco k'
  ],
  medio: [
    'contabilidade','contador','auditoria','perícia','compliance',
    'fraude fiscal','due diligence','jurisprudência','acórdão',
    'consulta tributária','instrução normativa','solução de consulta',
    'benefícios previdenciários','contribuições sociais'
  ],
  fracos: [
    'jogos','game','celebridade','famoso','esporte','entretenimento',
    'show','cantor','cantora','filme','série','bbb','futebol',
    'meme','tiktok','gadget','smartphone'
  ],
  brasil: [
    'brasil','receita federal','carf','stf','stj',
    'sefaz','pgfn','procuradoria da fazenda nacional',
    'governo federal','congresso','câmara','senado'
  ],
  contextoEco: [
    'tributação','carga tributária','arrecadação','orçamento',
    'déficit fiscal','responsabilidade fiscal','economia','pib',
    'reforma','política fiscal','isenção','renúncia fiscal'
  ]
};

// === Funções auxiliares ===
const norm = (s='') =>
  String(s).toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .replace(/https?:\/\/\S+/g,' ')
    .replace(/[^\p{L}\p{N}\s]/gu,' ')
    .replace(/\s+/g,' ')
    .trim();

const tokens = s => new Set(norm(s).split(' ').filter(t => t.length > 2));

const tokenSimilarity = (a,b) => {
  const A = tokens(a), B = tokens(b);
  const inter = [...A].filter(t => B.has(t)).length;
  return inter / (Math.max(A.size, B.size) || 1);
};

const hasAny = (text, list) => {
  const t = norm(text);
  return list.some(k => t.includes(norm(k)));
};

const hoursSince = (d) => {
  if (!d) return 1e9;
  const ts = new Date(d).getTime();
  return isNaN(ts) ? 1e9 : (Date.now() - ts) / 36e5;
};

const recencyMultiplier = (h) => {
  const lambda = Math.log(2) / DECAY_HALF_LIFE_H;
  return Math.exp(-lambda * h);
};

const scoreItem = (j) => {
  const title = j.title || '';
  const body = j['content:encoded'] || j.content || '';
  const snippet = j.contentSnippet || '';
  const cats = Array.isArray(j.categories)
    ? j.categories.map(c => typeof c === 'string' ? c : JSON.stringify(c)).join(' ')
    : (typeof j.categories === 'string' ? j.categories : '');
  const text = `${title} ${snippet} ${body} ${cats}`;

  let score = 0;
  const forte = hasAny(text, KW.forte);
  const contexto = hasAny(text, KW.contextoEco);

  if (forte) score += 40;
  if (hasAny(text, KW.medio)) score += 20;
  if (hasAny(text, KW.brasil)) score += 10;
  if (hasAny(text, KW.fracos)) score -= 50;

  if (forte && contexto) score += 30;

  const hasNumber = /\b\d+([.,]\d+)?\b/.test(text);
  const hasPercent = /%\b/.test(text);
  if (contexto && hasPercent) score += 15;
  else if (contexto && hasNumber) score += 5;

  score *= recencyMultiplier(hoursSince(j.isoDate || j.pubDate));
  return Math.max(0, Math.min(100, Math.round(score)));
};

// === Pipeline ===
const seenTitles = [];
const scoredItems = [];

for (const item of items) {
  const j = { ...item.json };
  const s = scoreItem(j);

  let dup = false;
  for (const prevTitle of seenTitles) {
    if (tokenSimilarity(prevTitle, j.title || '') >= DEDUPE_SIMILARITY) {
      dup = true; break;
    }
  }
  if (dup) continue;

  if (s >= THRESHOLD) {
    seenTitles.push(j.title || '');
    if (KEEP_SCORE_IN_OUTPUT) j._score = s;
    scoredItems.push({ json: j, _score: s });
  }
}

scoredItems.sort((a,b) => b._score - a._score);
return scoredItems.slice(0, MAX_ITENS).map(i => ({ json: i.json }));

// Coloque os possíveis campos que podem trazer a imagem direto do RSS
const possiveisCampos = [
  $json['media:content']?.url,
  $json['enclosure']?.url,
  $json['image'],
  $json['og:image']
];
const imagemDireta = possiveisCampos.find(x => !!x);

return {
  ...$json,
  imagem: imagemDireta || '' // Se não tiver, deixa vazio
};

return {
  ...$json,
  original: $json // salva todos os dados da notícia
};

const html = $json.data || $json.body || '';

const ogImageMatch = html.match(/<meta\s+property=["']og:image["']\s+content=["']([^"']+)["']/i);
let imagem = ogImageMatch ? ogImageMatch[1] : "";

if (!imagem) {
  const imgMatch = html.match(/<img[^>]+src=["']([^"']+)["']/i);
  if (imgMatch) {
    imagem = imgMatch[1]; // aqui mantém a URL achada
  }
}

// Recupera os campos originais da notícia de 'Backup dados da notícia1'
return {
  ...($('Backup dados da notícia').item.json.original || {}),
  imagem
};

